function makeSeed(input)
    if not input or not tostring(input) or input == "" then
        return math.random(math.abs(timer.getMilliSecCounter()))
    else
        input = tostring(input)

        local s = 0
        for i = 1, input:len() do
            s = s + string.byte(string.sub(input, i, i))
        end
        return s
    end
end
