screens={}
function addScreen(screen,...)
    if screen.active then
        return false
    else
        table.insert(screens,screen)
        screen.active=true
        screen:onLoad(...)
        invalidateScreen()
    end
end
function setScreen(screen,...)
    for i, s in pairs(screens) do
        if s.onRemove then s:onRemove() end
        s.active=false
    end
    screens={screen}
    invalidateScreen()
    collectgarbage()
    if screen.active then
        return false
    else
        screen.active=true
        screen:onLoad(...)
        return true
    end
end
function removeScreen(screen)
    for i, s in pairs(screens) do
        if s==screen then
            if s.onRemove then s:onRemove() end
            s.active=false
            table.remove(screens,i)
            invalidateScreen()
            collectgarbage()
            return true
        end
    end
    return false
end

Screen=class()
function Screen:init(option)
    function self:onLoad() end
    function self:onRemove() end
    if option=="gui" then
        function self:mouseMove(mx, my)
            for i, b in pairs(self.buttons) do
                if inRect({mx, my},{b[2],b[3],b[4],b[5]}) and i~=self.selButton then
                    self.selButton=i
                    invalidateScreen()
                    return
                end
            end
        end
    end
end

