            local searchBlock
            for dy = -2, 2 do
                for dx = -2, 2 do
                    if validField(x + dx, y + dy) then
                        searchBlock = world[x + dx][y + dy]
                        if (searchBlock == Block_wood or searchBlock == Block_trunk_wood) then
                            stayAlive = true
