            end
        end
        if rareSpaceWarning then gc:drawImage(guiTexture[21],152,WH-37) end
        if advCraftTable then gc:drawImage(guiTexture[26],WW-16,5) end

        -- armor bar
        local armor = 0
        for i = 37, 40 do
